// marshalAgentdefinitionsAgentDefinitionViewToAgentDefinitionViewResponseBody
// builds a value of type *AgentDefinitionViewResponseBody from a value of type
// *agentdefinitions.AgentDefinitionView.
func marshalAgentdefinitionsAgentDefinitionViewToAgentDefinitionViewResponseBody(v *agentdefinitions.AgentDefinitionView) *AgentDefinitionViewResponseBody {
	res := &AgentDefinitionViewResponseBody{
		ID:          v.ID,
		Name:        v.Name,
		ToolUrn:     v.ToolUrn,
		Model:       v.Model,
		Title:       v.Title,
		Description: v.Description,
		Instruction: v.Instruction,
		CreatedAt:   v.CreatedAt,
		UpdatedAt:   v.UpdatedAt,
	}
	if v.Tools != nil {
		res.Tools = make([]string, len(v.Tools))
		for i, val := range v.Tools {
			res.Tools[i] = val
		}
	} else {
		res.Tools = []string{}
	}

	return res
}
