// DecodeGetUnreadCountRequest returns a decoder for requests sent to the
// notifications getUnreadCount endpoint.
func DecodeGetUnreadCountRequest(mux goahttp.Muxer, decoder func(*http.Request) goahttp.Decoder) func(*http.Request) (*notifications.GetUnreadCountPayload, error) {
	return func(r *http.Request) (*notifications.GetUnreadCountPayload, error) {
		var (
			since            *string
			sessionToken     *string
			projectSlugInput *string
			err              error
		)
		sinceRaw := r.URL.Query().Get("since")
		if sinceRaw != "" {
			since = &sinceRaw
		}
		if since != nil {
			err = goa.MergeErrors(err, goa.ValidateFormat("since", *since, goa.FormatDateTime))
		}
		sessionTokenRaw := r.Header.Get("Gram-Session")
		if sessionTokenRaw != "" {
			sessionToken = &sessionTokenRaw
		}
		projectSlugInputRaw := r.Header.Get("Gram-Project")
		if projectSlugInputRaw != "" {
			projectSlugInput = &projectSlugInputRaw
		}
		if err != nil {
			return nil, err
		}
		payload := NewGetUnreadCountPayload(since, sessionToken, projectSlugInput)
		if payload.SessionToken != nil {
			if strings.Contains(*payload.SessionToken, " ") {
				// Remove authorization scheme prefix (e.g. "Bearer")
				cred := strings.SplitN(*payload.SessionToken, " ", 2)[1]
				payload.SessionToken = &cred
			}
		}
		if payload.ProjectSlugInput != nil {
			if strings.Contains(*payload.ProjectSlugInput, " ") {
				// Remove authorization scheme prefix (e.g. "Bearer")
				cred := strings.SplitN(*payload.ProjectSlugInput, " ", 2)[1]
				payload.ProjectSlugInput = &cred
			}
		}

		return payload, nil
	}
}

