// marshalNotificationsNotificationToNotificationResponseBody builds a value of
// type *NotificationResponseBody from a value of type
// *notifications.Notification.
func marshalNotificationsNotificationToNotificationResponseBody(v *notifications.Notification) *NotificationResponseBody {
	res := &NotificationResponseBody{
		ID:           v.ID,
		ProjectID:    v.ProjectID,
		Type:         string(v.Type),
		Level:        string(v.Level),
		Title:        v.Title,
		Message:      v.Message,
		ActorUserID:  v.ActorUserID,
		ResourceType: v.ResourceType,
		ResourceID:   v.ResourceID,
		ArchivedAt:   v.ArchivedAt,
		CreatedAt:    v.CreatedAt,
	}

	return res
}
