-- Create "mcp_install_page_metadata" table
CREATE TABLE "mcp_install_page_metadata" ("id" uuid NOT NULL DEFAULT generate_uuidv7(), "toolset_id" uuid NOT NULL, "external_documentation_url" text NULL, "logo_id" uuid NULL, "created_at" timestamptz NOT NULL DEFAULT clock_timestamp(), "updated_at" timestamptz NOT NULL DEFAULT clock_timestamp(), PRIMARY KEY ("id"), CONSTRAINT "mcp_install_page_metadata_asset_id" FOREIGN KEY ("logo_id") REFERENCES "assets" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "mcp_install_page_metadata_toolset_id" FOREIGN KEY ("toolset_id") REFERENCES "toolsets" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "mcp_install_page_metadata_toolset_id_key" UNIQUE ("toolset_id"));
