-- Create "mcp_metadata" table
CREATE TABLE "mcp_metadata" ("id" uuid NOT NULL DEFAULT generate_uuidv7(), "toolset_id" uuid NOT NULL, "project_id" uuid NOT NULL, "external_documentation_url" text NULL, "logo_id" uuid NULL, "created_at" timestamptz NOT NULL DEFAULT clock_timestamp(), "updated_at" timestamptz NOT NULL DEFAULT clock_timestamp(), PRIMARY KEY ("id"), CONSTRAINT "mcp_metadata_toolset_id_key" UNIQUE ("toolset_id"), CONSTRAINT "mcp_metadata_logo_id" FOREIGN KEY ("logo_id") REFERENCES "assets" ("id") ON UPDATE NO ACTION ON DELETE SET NULL, CONSTRAINT "mcp_metadata_project_id" FOREIGN KEY ("project_id") REFERENCES "projects" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "mcp_metadata_toolset_id" FOREIGN KEY ("toolset_id") REFERENCES "toolsets" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
